import React, { useContext } from 'react';
import PropTypes from 'prop-types';
import { EventContext, Styled } from 'direflow-component';
import styles from './Button.css';

const App = ({ variant, theme, size, title }) => {
  const dispatch = useContext(EventContext);

  const handleClick = () => {
    const event = new Event('onClick');
    dispatch(event);
  };

  const themeClass = `theme-${theme || "light"}`;
  const variantClass = `button--${variant || "primary"}`;
  const sizeClass = `button--${size || "medium"}`;

  return (
    <Styled styles={styles}>
      <button
        class={`${["button", themeClass, variantClass, sizeClass].join(" ")}`}
        onClick={handleClick}
      >
        {title}
      </button>
    </Styled>
  );
};

App.defaultProps = {
  componentTitle: 'Lp Button',
}

App.propTypes = {
  componentTitle: PropTypes.string,
  sampleList: PropTypes.array,
};

export default App;
